using System;
using System.Diagnostics;
using System.Windows.Data;
using System.Windows.Markup;

namespace Localization.WPF
{
    /// <summary>
    /// Markup extension that creates an untyped array with up to 51 items.
    /// </summary>
    /// <remarks>
    /// Example:
    /// <code>
    /// Text="{Tr 'MainWindow.TextBlock',
    ///           FormatString='[0] {1} {2} {3} {4}',
    ///           FormatArgs={MakeArray 'asdf',
    ///                                 {Binding Text},
    ///                                 'Hello',
    ///                                 'World!'}}"
    /// </code>
    /// </remarks>
    [DebuggerDisplay("{_items}")]
    public class MakeArrayExtension : MarkupExtension
    {
        #region Constructors
        /// <summary>
        /// Default Constructor
        /// </summary>
        public MakeArrayExtension() { _items = Array.Empty<object>(); }
        #region ItemsConstructors
        private MakeArrayExtension(object?[] items) { _items = items; }
        //^ reference count is the total number of items that can be added using this markup extension

        // To expand this, duplicate the longest line & add however many elements you want to add, then use the following regex substitution expr on that line until you have filled in the gap
        //  Expr: ^(\s*public MakeArrayExtension\(object\? item0(?:, object\? item\d+)+)(?:, object\? item\d+)(\) : this\(new\[\] { item0(?:, item\d+)+)(?:, item\d+)( }\) { })
        //  Repl: $1$2$3\n$0

#pragma warning disable CS1591 // Missing XML comment for publicly visible type or member
        public MakeArrayExtension(object? item0) : this(new[] { item0 }) { }
        public MakeArrayExtension(object? item0, object? item1) : this(new[] { item0, item1 }) { }
        public MakeArrayExtension(object? item0, object? item1, object? item2) : this(new[] { item0, item1, item2 }) { }
        public MakeArrayExtension(object? item0, object? item1, object? item2, object? item3) : this(new[] { item0, item1, item2, item3 }) { }
